void gerarFantasmapa(char s[ROWS][COLS], char d[ROWS][COLS])
{
    for (int i = 0; i < ROWS; i++)
    {
        for (int j = 0; j < COLS; j++)
        {
            d[i][j] = s[i][j] == '#' ? '#' : ' ';
        }
    }
}

